INSERT INTO departments (id, title, slug, description) VALUES ( 1,'Hagfræðideild', 'hagfraedideild', 'Menntun í Hagfræðideild er greiðasta leiðin til þátttöku, rannsókna og skilnings á hagkerfi okkar. Kennslan stendur á sterkum grunni og er markmið námsins að veita nemendum góðan undirbúning í hagfræði, stærðfræði og tölfræði og möguleika á sérhæfingu í öðrum greinum.');
INSERT INTO departments (id, title, slug, description) VALUES ( 2,'Iðnaðarverkfræði-, vélaverkfræði- og tölvunarfræðideild', 'ivt', 'Deildin býður upp á fjölbreytt námsframboð verkfræði- og tölvunarfræðigreina. Þessar greinar eiga það sameiginlegt að vera afar hagnýtar og þýðingarmiklar við uppbyggingu og rekstur nútíma þjóðfélaga.');
INSERT INTO departments (id, title, slug, description) VALUES ( 3, 'Íslensku og menningardeild', 'islenska', 'Íslensku- og menningardeild er ein fjögurra deilda á Hugvísindasviði og er ein sú fjölmennasta innan Háskólans. Í deildinni eru nú kenndar milli 10 og 20 greinar, sumar bæði á grunn- og framhaldsstigi, en sumar eingöngu í grunnnámi og aðrar eingöngu í framhaldsnámi.');
INSERT INTO departments (id, title, slug, description) VALUES ( 4, 'Læknadeild', 'laeknadeild', 'Læknadeild annast kennslu og rannsóknir í geislafræði, heilbrigðisgagnafræði, lífeindafræði, læknisfræði og sjúkraþjálfunarfræði. Deildin starfar í nánu samstarfi við Landspítala og fleiri heilbrigðisstofnanir innanlands og utan. Læknadeild er leiðandi í kennslu og rannsóknum í heilbrigðisvísindum hér á landi.');
INSERT INTO departments (id, title, slug, description) VALUES ( 5, 'Sálfræðideild', 'salfraedideild', 'Sálfræði er vísindagrein um huga, heila og hátterni. Grundvöllur sálfræðinnar er fjölbreytilegt rannsóknastarf en greinin er víða hagnýtt, meðal annars í skólum, meðferðarstarfi og atvinnulífi.');
INSERT INTO departments (id, title, slug, description) VALUES ( 6, 'Viðskiptafræði', 'vidskiptafraedi', 'Viðskiptafræðideild hefur gegnt forystuhlutverki í menntun stjórnenda og sérfræðinga á sviði viðskiptafræði í áratugi. Deildin hefur lagt metnað sinn í að miðla alþjóðlega viðurkenndri þekkingu með því að vera leiðandi í rannsóknum, kennslu og þjónustu við íslenskt atvinnulíf.');
